package Opps;

import java.util.Scanner;

public class Primenumber {

	public static void main(String[] args) {
		
		Scanner kb =new Scanner (System.in);
		
		
		int prime, i;
		prime = kb.nextInt();
		
		
	for (i=0; i<prime ;i++)
	{
		
		if (i!=0)
		{
		System.out.println(i/prime)	;
		}
		System.out.println(i);
	}

		// TODO Auto-generated method stub

	}

}
